import React, { useState } from 'react';
import {
  Box,
  Card,
  Typography,
  Grid,
  TextField,
  Button,
  IconButton,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Tooltip,
  CircularProgress,
} from '@mui/material';
import {
  Favorite as HeartIcon,
  DeviceThermometerIcon,
  MonitorWeight as WeightIcon,
  Speed as PressureIcon,
  Add as AddIcon,
  Delete as DeleteIcon,
  Timeline as TimelineIcon,
} from '@mui/icons-material';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip as ChartTooltip, Legend, ResponsiveContainer } from 'recharts';

const HealthMetrics = () => {
  const [metrics, setMetrics] = useState({
    bloodPressure: { systolic: '', diastolic: '', pulse: '' },
    temperature: '',
    weight: '',
    bloodSugar: '',
  });

  const [history, setHistory] = useState([]);

  const handleMetricChange = (metric, value) => {
    setMetrics(prev => ({
      ...prev,
      [metric]: value,
    }));
  };

  const handleSave = () => {
    const newEntry = {
      ...metrics,
      timestamp: new Date().toISOString(),
    };
    setHistory(prev => [...prev, newEntry]);
    // Reset form
    setMetrics({
      bloodPressure: { systolic: '', diastolic: '', pulse: '' },
      temperature: '',
      weight: '',
      bloodSugar: '',
    });
  };

  const getMetricStatus = (metric, value) => {
    const ranges = {
      systolic: { low: 90, normal: 120, high: 140 },
      diastolic: { low: 60, normal: 80, high: 90 },
      pulse: { low: 60, normal: 100, high: 100 },
      temperature: { low: 36.1, normal: 37.2, high: 38 },
      bloodSugar: { low: 70, normal: 140, high: 180 },
    };

    if (!ranges[metric]) return 'normal';
    if (value < ranges[metric].low) return 'low';
    if (value > ranges[metric].high) return 'high';
    return 'normal';
  };

  return (
    <Box sx={{ mt: 4 }}>
      <Typography variant="h5" gutterBottom sx={{ mb: 3, display: 'flex', alignItems: 'center', gap: 1 }}>
        <TimelineIcon color="primary" />
        Health Metrics Tracking
      </Typography>

      <Grid container spacing={3}>
        {/* Current Metrics Input */}
        <Grid item xs={12} md={6}>
          <Card sx={{ p: 3 }}>
            <Typography variant="h6" gutterBottom>
              Record New Measurements
            </Typography>

            <Grid container spacing={2}>
              <Grid item xs={12}>
                <Box sx={{ display: 'flex', gap: 2 }}>
                  <TextField
                    label="Systolic"
                    type="number"
                    value={metrics.bloodPressure.systolic}
                    onChange={(e) => handleMetricChange('bloodPressure', {
                      ...metrics.bloodPressure,
                      systolic: e.target.value
                    })}
                    InputProps={{
                      endAdornment: <Typography variant="caption">mmHg</Typography>
                    }}
                  />
                  <TextField
                    label="Diastolic"
                    type="number"
                    value={metrics.bloodPressure.diastolic}
                    onChange={(e) => handleMetricChange('bloodPressure', {
                      ...metrics.bloodPressure,
                      diastolic: e.target.value
                    })}
                    InputProps={{
                      endAdornment: <Typography variant="caption">mmHg</Typography>
                    }}
                  />
                  <TextField
                    label="Pulse"
                    type="number"
                    value={metrics.bloodPressure.pulse}
                    onChange={(e) => handleMetricChange('bloodPressure', {
                      ...metrics.bloodPressure,
                      pulse: e.target.value
                    })}
                    InputProps={{
                      endAdornment: <Typography variant="caption">bpm</Typography>
                    }}
                  />
                </Box>
              </Grid>

              <Grid item xs={12} sm={6}>
                <TextField
                  fullWidth
                  label="Temperature"
                  type="number"
                  value={metrics.temperature}
                  onChange={(e) => handleMetricChange('temperature', e.target.value)}
                  InputProps={{
                    endAdornment: <Typography variant="caption">Â°C</Typography>
                  }}
                />
              </Grid>

              <Grid item xs={12} sm={6}>
                <TextField
                  fullWidth
                  label="Weight"
                  type="number"
                  value={metrics.weight}
                  onChange={(e) => handleMetricChange('weight', e.target.value)}
                  InputProps={{
                    endAdornment: <Typography variant="caption">kg</Typography>
                  }}
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Blood Sugar"
                  type="number"
                  value={metrics.bloodSugar}
                  onChange={(e) => handleMetricChange('bloodSugar', e.target.value)}
                  InputProps={{
                    endAdornment: <Typography variant="caption">mg/dL</Typography>
                  }}
                />
              </Grid>

              <Grid item xs={12}>
                <Button
                  variant="contained"
                  fullWidth
                  onClick={handleSave}
                  startIcon={<AddIcon />}
                >
                  Save Measurements
                </Button>
              </Grid>
            </Grid>
          </Card>
        </Grid>

        {/* Metrics History */}
        <Grid item xs={12} md={6}>
          <Card sx={{ p: 3 }}>
            <Typography variant="h6" gutterBottom>
              Recent History
            </Typography>
            <TableContainer component={Paper} sx={{ maxHeight: 400 }}>
              <Table stickyHeader size="small">
                <TableHead>
                  <TableRow>
                    <TableCell>Date</TableCell>
                    <TableCell>BP</TableCell>
                    <TableCell>Pulse</TableCell>
                    <TableCell>Temp</TableCell>
                    <TableCell>Weight</TableCell>
                    <TableCell>Blood Sugar</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {history.map((entry, index) => (
                    <TableRow key={index}>
                      <TableCell>{new Date(entry.timestamp).toLocaleDateString()}</TableCell>
                      <TableCell>
                        {entry.bloodPressure.systolic}/{entry.bloodPressure.diastolic}
                      </TableCell>
                      <TableCell>{entry.bloodPressure.pulse}</TableCell>
                      <TableCell>{entry.temperature}</TableCell>
                      <TableCell>{entry.weight}</TableCell>
                      <TableCell>{entry.bloodSugar}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </Card>
        </Grid>

        {/* Trends Chart */}
        <Grid item xs={12}>
          <Card sx={{ p: 3 }}>
            <Typography variant="h6" gutterBottom>
              Trends
            </Typography>
            <Box sx={{ height: 300 }}>
              <ResponsiveContainer width="100%" height="100%">
                <LineChart data={history}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis
                    dataKey="timestamp"
                    tickFormatter={(timestamp) => new Date(timestamp).toLocaleDateString()}
                  />
                  <YAxis />
                  <ChartTooltip />
                  <Legend />
                  <Line type="monotone" dataKey="bloodPressure.systolic" stroke="#2563eb" name="Systolic" />
                  <Line type="monotone" dataKey="bloodPressure.diastolic" stroke="#7c3aed" name="Diastolic" />
                  <Line type="monotone" dataKey="bloodPressure.pulse" stroke="#ef4444" name="Pulse" />
                  <Line type="monotone" dataKey="temperature" stroke="#f59e0b" name="Temperature" />
                  <Line type="monotone" dataKey="bloodSugar" stroke="#10b981" name="Blood Sugar" />
                </LineChart>
              </ResponsiveContainer>
            </Box>
          </Card>
        </Grid>
      </Grid>
    </Box>
  );
};

export default HealthMetrics;
